import { createContext, } from "react";

// import useDataFetching from "../hooks/useDataFetching";

export const ItemsContext = createContext();

export const ItemsContextProvider = ({ children }) => {

    // const [loading, error, data] = useDataFetching('https://my-json-server.typicode.com/PackPublishing/React-Projects-Second-Edition/lists');

    const [loading, setLoading] = useState(true);

    const [items, setItems] = useState([]);

    const [error, setError] = useState('');

    const fetchItems = useCallback( async (listId) => {

        try {

            const data = await fetch(`https://my-json-server.typicode.com/PackPublishing/React-Projects-Second-Edition/lists/${listId}/items`);

            const result = await data.json();

            if (result) {
                setItems(result);
                setLoading(false)
            }
        } catch (e) {

            setLoading(false);
            setError(e.message);
        }
    }, []);

    return (

        <ItemsContext.Provider value={{ items, loading, error, fetchItems }}>
            {children}
        </ItemsContext.Provider>
    );

};

export default ItemsContext;
